import React, { useRef, useEffect, useState, useCallback } from 'react';

const Canvas = ({ socket }) => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [tool, setTool] = useState('pen');
  const [color, setColor] = useState('#000000');
  const [size, setSize] = useState(5);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    // Set canvas size
    canvas.width = canvas.offsetWidth;
    canvas.height = canvas.offsetHeight;

    // Set drawing properties
    context.lineCap = 'round';
    context.lineJoin = 'round';

    // Handle incoming draw events
    socket?.on('draw-event', (data) => {
      drawLine(context, data);
    });

    return () => {
      socket?.off('draw-event');
    };
  }, [socket]);

  const drawLine = (context, data) => {
    context.globalCompositeOperation = data.tool === 'eraser' ? 'destination-out' : 'source-over';
    context.strokeStyle = data.color;
    context.lineWidth = data.size;

    context.beginPath();
    context.moveTo(data.x0, data.y0);
    context.lineTo(data.x1, data.y1);
    context.stroke();
  };

  const startDrawing = useCallback((e) => {
    setIsDrawing(true);
    const rect = canvasRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Store the starting point
    canvasRef.current.lastX = x;
    canvasRef.current.lastY = y;
  }, []);

  const draw = useCallback((e) => {
    if (!isDrawing) return;

    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const drawData = {
      x0: canvas.lastX,
      y0: canvas.lastY,
      x1: x,
      y1: y,
      color,
      size,
      tool
    };

    // Draw locally
    const context = canvas.getContext('2d');
    drawLine(context, drawData);

    // Emit to other users
    socket?.emit('draw-event', drawData);

    canvas.lastX = x;
    canvas.lastY = y;
  }, [isDrawing, color, size, tool, socket]);

  const stopDrawing = useCallback(() => {
    setIsDrawing(false);
  }, []);

  const clearCanvas = () => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    context.clearRect(0, 0, canvas.width, canvas.height);

    // Emit clear event to other users
    socket?.emit('draw-event', { type: 'clear' });
  };

  return (
    <div className="h-full flex flex-col">
      {/* Toolbar */}
      <div className="bg-white border-b border-gray-200 p-4 flex items-center space-x-4">
        <div className="flex items-center space-x-2">
          <label className="text-sm font-medium">Tool:</label>
          <select
            value={tool}
            onChange={(e) => setTool(e.target.value)}
            className="border border-gray-300 rounded px-2 py-1"
          >
            <option value="pen">Pen</option>
            <option value="eraser">Eraser</option>
          </select>
        </div>

        <div className="flex items-center space-x-2">
          <label className="text-sm font-medium">Color:</label>
          <input
            type="color"
            value={color}
            onChange={(e) => setColor(e.target.value)}
            className="w-8 h-8 border border-gray-300 rounded"
          />
        </div>

        <div className="flex items-center space-x-2">
          <label className="text-sm font-medium">Size:</label>
          <input
            type="range"
            min="1"
            max="50"
            value={size}
            onChange={(e) => setSize(parseInt(e.target.value))}
            className="w-20"
          />
          <span className="text-sm w-8">{size}</span>
        </div>

        <button
          onClick={clearCanvas}
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          Clear
        </button>
      </div>

      {/* Canvas */}
      <canvas
        ref={canvasRef}
        className="flex-1 cursor-crosshair bg-white"
        onMouseDown={startDrawing}
        onMouseMove={draw}
        onMouseUp={stopDrawing}
        onMouseLeave={stopDrawing}
      />
    </div>
  );
};

export default Canvas;
