import React, { useState } from 'react';

const ShareWorkspace = ({ roomId }) => {
  const [showShare, setShowShare] = useState(false);
  const [copied, setCopied] = useState(false);

  const shareUrl = `${window.location.origin}/workspace/${roomId}`;

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(shareUrl);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  const copyRoomId = async () => {
    try {
      await navigator.clipboard.writeText(roomId);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy: ', err);
    }
  };

  const shareViaEmail = () => {
    const subject = encodeURIComponent('Join my collaborative workspace');
    const body = encodeURIComponent(`Hi! I'd like to invite you to collaborate with me on a shared workspace.\n\nClick this link to join: ${shareUrl}\n\nOr use Room ID: ${roomId}\n\nSee you there!`);
    window.open(`mailto:?subject=${subject}&body=${body}`);
  };

  const shareViaWhatsApp = () => {
    const text = encodeURIComponent(`Join my collaborative workspace! ${shareUrl} or use Room ID: ${roomId}`);
    window.open(`https://wa.me/?text=${text}`);
  };

  return (
    <div className="relative">
      <button
        onClick={() => setShowShare(!showShare)}
        className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 flex items-center space-x-2"
      >
        <span>ðŸ”—</span>
        <span>Share</span>
      </button>

      {showShare && (
        <div className="absolute top-12 right-0 w-80 bg-white border border-gray-200 rounded-lg shadow-xl z-50 p-4">
          <div className="space-y-4">
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                Share Workspace
              </h3>
              <p className="text-sm text-gray-600">
                Invite others to collaborate by sharing the link or room ID
              </p>
            </div>

            {/* Share URL */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Share Link
              </label>
              <div className="flex space-x-2">
                <input
                  type="text"
                  value={shareUrl}
                  readOnly
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-md bg-gray-50 text-sm"
                />
                <button
                  onClick={copyToClipboard}
                  className={`px-3 py-2 rounded-md text-sm ${
                    copied
                      ? 'bg-green-500 text-white'
                      : 'bg-blue-500 text-white hover:bg-blue-600'
                  }`}
                >
                  {copied ? 'âœ“' : 'ðŸ“‹'}
                </button>
              </div>
            </div>

            {/* Room ID */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Room ID
              </label>
              <div className="flex space-x-2">
                <input
                  type="text"
                  value={roomId}
                  readOnly
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-md bg-gray-50 text-sm font-mono"
                />
                <button
                  onClick={copyRoomId}
                  className={`px-3 py-2 rounded-md text-sm ${
                    copied
                      ? 'bg-green-500 text-white'
                      : 'bg-blue-500 text-white hover:bg-blue-600'
                  }`}
                >
                  {copied ? 'âœ“' : 'ðŸ“‹'}
                </button>
              </div>
            </div>

            {/* Share Options */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Share via
              </label>
              <div className="flex space-x-2">
                <button
                  onClick={shareViaEmail}
                  className="flex-1 px-3 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600 text-sm"
                >
                  ðŸ“§ Email
                </button>
                <button
                  onClick={shareViaWhatsApp}
                  className="flex-1 px-3 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 text-sm"
                >
                  ðŸ“± WhatsApp
                </button>
              </div>
            </div>

            {/* QR Code Option */}
            <div className="border-t pt-4">
              <button
                onClick={() => {
                  const qrUrl = `https://api.qrserver.com/v1/create-qr-code/?size=200x200&data=${encodeURIComponent(shareUrl)}`;
                  window.open(qrUrl, '_blank');
                }}
                className="w-full px-3 py-2 bg-purple-500 text-white rounded-md hover:bg-purple-600 text-sm"
              >
                ðŸ“± Generate QR Code
              </button>
            </div>

            {/* Close button */}
            <div className="border-t pt-4">
              <button
                onClick={() => setShowShare(false)}
                className="w-full px-3 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 text-sm"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Click outside to close */}
      {showShare && (
        <div
          className="fixed inset-0 z-40"
          onClick={() => setShowShare(false)}
        />
      )}
    </div>
  );
};

export default ShareWorkspace;
