import React, { useState, useEffect } from 'react';
import { useParams, useSearchParams } from 'react-router-dom';
import ExcalidrawCanvas from '../components/ExcalidrawCanvas';
import UserList from '../components/UserList';
import VideoCall from '../components/VideoCall';
import Chat from '../components/Chat';
import ShareWorkspace from '../components/ShareWorkspace';
import TopNavigation from '../components/TopNavigation';
import { useSocket } from '../sockets/useSocket';
import { useWorkspace } from '../sockets/useWorkspace';

const Workspace = () => {
  const { roomId } = useParams();
  const [searchParams] = useSearchParams();
  const username = searchParams.get('username') || 'Guest';

  const [users, setUsers] = useState([]);
  const [isVideoCallActive, setIsVideoCallActive] = useState(false);
  const [isChatOpen, setIsChatOpen] = useState(true);
  const [isShareModalOpen, setIsShareModalOpen] = useState(false);
  const [workspaceName, setWorkspaceName] = useState('');

  const socket = useSocket(roomId, { username });
  const { workspaceData, loading: workspaceLoading } = useWorkspace(roomId);

  useEffect(() => {
    if (!socket) return;

    // Handle room state updates
    socket.on('room-state', (data) => {
      setUsers(data.users);
    });

    socket.on('user-joined', (data) => {
      setUsers(prev => [...prev, data]);
    });

    socket.on('user-left', (data) => {
      setUsers(prev => prev.filter(user => user.id !== data.userId));
    });

    return () => {
      socket.off('room-state');
      socket.off('user-joined');
      socket.off('user-left');
    };
  }, [socket]);

  const toggleVideoCall = () => {
    setIsVideoCallActive(!isVideoCallActive);
  };

  if (!socket || workspaceLoading) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-lg">Loading workspace...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div>
              <h1 className="text-xl font-semibold text-gray-900">
                {workspaceData?.name || `Workspace: ${roomId.slice(0, 8)}...`}
              </h1>
              <p className="text-sm text-gray-500">
                Logged in as: {username}
              </p>
            </div>
            <div className="flex items-center space-x-4">
              <ShareWorkspace roomId={roomId} />
              <button
                onClick={toggleVideoCall}
                className={`px-4 py-2 rounded-md ${
                  isVideoCallActive
                    ? 'bg-red-500 hover:bg-red-600 text-white'
                    : 'bg-blue-500 hover:bg-blue-600 text-white'
                }`}
              >
                {isVideoCallActive ? 'End Call' : 'Start Video Call'}
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="flex h-[calc(100vh-80px)]">
        {/* Sidebar */}
        <div className="w-64 bg-white border-r border-gray-200 p-4">
          <UserList users={users} currentUserId={socket.id} />
        </div>

        {/* Canvas Area */}
        <div className="flex-1 relative">
          <ExcalidrawCanvas socket={socket} workspaceData={workspaceData} />

          {/* Video Call Overlay */}
          {isVideoCallActive && (
            <div className="absolute top-4 right-4 z-10">
              <VideoCall socket={socket} users={users} />
            </div>
          )}
        </div>
      </div>

      {/* Chat Component */}
      <Chat 
        socket={socket} 
        currentUser={{ username, id: socket?.id }} 
        workspaceData={workspaceData}
      />
    </div>
  );
};

export default Workspace;
