import React, { useState, useEffect, useRef } from 'react';

const Chat = ({ socket, currentUser, workspaceData }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isMinimized, setIsMinimized] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Load chat history when workspace data is available
  useEffect(() => {
    if (workspaceData?.chatHistory) {
      const chatHistory = workspaceData.chatHistory.map((msg, index) => ({
        id: `history-${index}`,
        username: msg.username,
        message: msg.message,
        timestamp: msg.timestamp,
        userId: msg.userId
      }));
      setMessages(chatHistory);
    }
  }, [workspaceData]);

  useEffect(() => {
    if (!socket) return;

    // Listen for incoming messages
    socket.on('chat-message', (data) => {
      setMessages(prev => [...prev, {
        id: Date.now(),
        username: data.username,
        message: data.message,
        timestamp: data.timestamp,
        userId: data.userId
      }]);
    });

    // Listen for user join/leave notifications
    socket.on('user-notification', (data) => {
      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'notification',
        message: data.message,
        timestamp: data.timestamp
      }]);
    });

    return () => {
      socket.off('chat-message');
      socket.off('user-notification');
    };
  }, [socket]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !socket) return;

    const messageData = {
      username: currentUser.username,
      message: newMessage.trim(),
      timestamp: new Date().toISOString(),
      userId: socket.id
    };

    // Add message to local state immediately
    setMessages(prev => [...prev, {
      id: Date.now(),
      ...messageData
    }]);

    // Send to other users
    socket.emit('chat-message', messageData);
    setNewMessage('');
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  if (isMinimized) {
    return (
      <div className="fixed bottom-4 right-4 z-50">
        <button
          onClick={() => setIsMinimized(false)}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg shadow-lg hover:bg-blue-600 flex items-center space-x-2"
        >
          <span>💬</span>
          <span>Chat</span>
          {messages.length > 0 && (
            <span className="bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
              {messages.length > 99 ? '99+' : messages.length}
            </span>
          )}
        </button>
      </div>
    );
  }

  return (
    <div className="fixed bottom-4 right-4 w-80 h-96 bg-white border border-gray-300 rounded-lg shadow-xl z-50 flex flex-col">
      {/* Header */}
      <div className="bg-blue-500 text-white px-4 py-3 rounded-t-lg flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <span>💬</span>
          <span className="font-medium">Chat</span>
        </div>
        <button
          onClick={() => setIsMinimized(true)}
          className="text-white hover:text-gray-200"
        >
          ➖
        </button>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-3 space-y-2">
        {messages.length === 0 ? (
          <div className="text-center text-gray-500 text-sm">
            No messages yet. Start the conversation!
          </div>
        ) : (
          messages.map((msg) => (
            <div key={msg.id} className="space-y-1">
              {msg.type === 'notification' ? (
                <div className="text-center text-xs text-gray-500 italic">
                  {msg.message}
                </div>
              ) : (
                <div className={`flex flex-col ${
                  msg.userId === socket?.id ? 'items-end' : 'items-start'
                }`}>
                  <div className={`max-w-xs px-3 py-2 rounded-lg ${
                    msg.userId === socket?.id
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-100 text-gray-800'
                  }`}>
                    {msg.userId !== socket?.id && (
                      <div className="text-xs font-medium mb-1">
                        {msg.username}
                      </div>
                    )}
                    <div className="text-sm">{msg.message}</div>
                  </div>
                  <div className="text-xs text-gray-500 mt-1">
                    {formatTime(msg.timestamp)}
                  </div>
                </div>
              )}
            </div>
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <form onSubmit={sendMessage} className="p-3 border-t border-gray-200">
        <div className="flex space-x-2">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message..."
            className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            maxLength={500}
          />
          <button
            type="submit"
            disabled={!newMessage.trim()}
            className="px-3 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 disabled:bg-gray-300 disabled:cursor-not-allowed"
          >
            📤
          </button>
        </div>
      </form>
    </div>
  );
};

export default Chat;
